import React, { useState, useEffect } from "react";
import { useUserAuth } from "../context/UserAuthContext";
import './AdminPannel.css'

const URL = 'https://jsonplaceholder.typicode.com/users'

function useAPi(url) {
  const [data, setData] = useState([])
  const { logOut, user, role } = useUserAuth();
  const handleLogout = async () => {
    try {
      await logOut();
      navigate("/");
    } catch (error) {
      console.log(error.message);
    }
  };

  useEffect(() => {
    getData()
  }, [])

  const getData = async () => {
    const response = await fetch("http://localhost:8080/users", {
     method: "GET",
     headers: {
       "Content-Type": "application/json"
     }
   })
   .then((data) => {
    if (!data.ok){
      throw new Error(data.json());
    }
    return data.json();
    })
    .then((data) => {
      console.log(data);
      return data;
    });
    console.log(response)
    setData(response)
  }

  const removeData = async (id) => {
    const response = await fetch('http://localhost:8080/user/'+id, {
     method: "DELETE",
     headers: {
       "Content-Type": "application/json"
     }
   })
   .then((data) => {
    if (!data.ok){
      throw new Error(data.json());
    }
    return data;
    })

    console.log(response)
    const del = data.filter((item) => id !== item.id)
    setData(del)
  }

  return { data, removeData }
}


const AdminPannel = () => {
     const { data, removeData } = useAPi(URL)

    const renderHeader = () => {
        let headerElement = ['id', 'name', 'username','email', 'operation']

        return headerElement.map((key, index) => {
            return <th key={index}>{key.toUpperCase()}</th>
        })
    }

    const renderBody = () => {
        return data && data.map(({ id, name, username, email }) => {
            return (
                <tr key={id}>
                    <td>{id}</td>
                    <td>{name}</td>
                    <td>{username}</td>
                    <td>{email}</td>
                    <td className='operation'>
                        <button className='button' onClick={() => removeData(id)}>Delete</button>
                    </td>
                </tr>
            )
        })
    }

    return (
        <>
            <h1 id='title'>Utilizatori</h1>
            <table id='employee'>
                <thead>
                    <tr>{renderHeader()}</tr>
                </thead>
                <tbody>
                    {renderBody()}
                </tbody>
            </table>
        </>
    )
}


export default AdminPannel;
